scriptlet.script:
/*
   This scriplet constructs the command lines to be executed in SCVMM for deploying a VM. 
   Generally, it consists of the following steps:
   1. Input Data Preparation. Get the inputs from OO Input and do the ncessary conversion.
   2. Create a temporary VM Template, and set the parameters - Hostname, VMLocation, IP addresses.
   3. Cerate a VMConfiguration based on that VM Template, and then create a VM with updated Tshirt size settings and associated Guest OS Profile.
   4. Set VM's VLAN and VM Networks.
   5. Extend OS disk if necessary, and add the first data disk.
   6. Power on the VM. 

   Note:
   1. A simple strategy is used to select a VMHost from the cluster. The VM is supposed to tranfer to the proper VMHost according to the strategy setup in SCVMM.
   2. Static IPs are fetched from Elastic IP and pinned in the SCVMM Pool.
   3. OS Disk is never shrinked if the target TShirt size implies a smaller disk0.
*/

function getInputValue(inputName) {
    if ((typeof(scriptletContext.get(inputName)) != "undefined") && (scriptletContext.get(inputName) != ""))
        return scriptletContext.get(inputName);
    else
        return null;
}

var vmHostName = scriptletContext.getLocal("vmHostName");
///// ram and cpu params
var memGB = scriptletContext.getLocal("memGB");
var cpuCount = scriptletContext.getLocal("cpuCount");
///// disk params
var disk0GB = scriptletContext.getLocal("disk0GB"); var dataDiskMB=1024*scriptletContext.getLocal("dataDiskGB")*1;

var commands = [];
commands.push("$vmTemplateName=\""+ scriptletContext.getLocal("vmTemplateName") + "\"");
commands.push("$guestOSProfileHashTable="+ scriptletContext.get("scvmm/guestOSProfiles") );
commands.push("$vmName=\"" + scriptletContext.getLocal("vmName") + "\"");
commands.push("$vmHostName=\"" + vmHostName + "\"");
commands.push("$vmDomainName=\"" + scriptletContext.get("pc.dns.machine.domain") + "\"");
commands.push("$vmComputerName=$vmHostName + '.' + $vmDomainName");
commands.push("$hostGroupName=\"" + scriptletContext.getLocal("hostGroupName") + "\"");
commands.push("$vmPath=\"" + scriptletContext.getLocal("vmPath") + "\"");
/////  vlan params
commands.push("$virtualNetwork=\""+ scriptletContext.get("scvmm/network.virtualnetwork") +"\"");

commands.push("$vmNetworkBURName=\""+ scriptletContext.get("scvmm/network.vmnetwork.BUR") +"\"");

commands.push("$vmNetworkCustomerName=\""+ scriptletContext.get("scvmm/network.vmnetwork.customer") +"\"");

commands.push(" $macAddressPoolName=\"" + scriptletContext.get("scvmm/network.mac_address_pool") + "\"");
var backupVlan = scriptletContext.getLocal("backupVlan");
var backupVlanID = scriptletContext.getLocal("backupVlanID");
var backupIP = scriptletContext.getLocal("backupIP");
var vlan1 = scriptletContext.getLocal("vlan1");
var vlan1ID = scriptletContext.getLocal("vlan1ID");
var vlan1IP = scriptletContext.getLocal("vlan1IP");
var vlan2 = getInputValue("vlan2");
var vlan2ID = getInputValue("vlan2ID");
var vlan2IP = getInputValue("vlan2IP");

commands.push("$psBackupVlan=\"" + backupVlan + "\""); commands.push("$psVlan1=\"" + vlan1 + "\""); commands.push("$psVlan2=\"" + vlan2 + "\"");
commands.push("$psBackupVlanID=" + backupVlanID); commands.push("$psVlan1ID=" + vlan1ID); if( vlan2ID != null) {commands.push("$psVlan2ID=" + vlan2ID); }
commands.push("$psBackupIP=\"" + backupIP + "\""); commands.push("$psVlan1IP=\"" + vlan1IP + "\""); commands.push(" $ipArray = $psVlan1IP,$psBackupIP ");
if( vlan2ID != null) { 
	commands.push("$psVlan2IP=\"" + vlan2IP + "\"");
	commands.push(" $ipArray += $psVlan2IP ");
}
commands.push("$tshirtSize=@{ram="+ (memGB * 1024)+ "; cpu=" + cpuCount + "; osDisk="+disk0GB+ "; dataDisk=" + dataDiskMB + "}" );
//////////////////////////////////////////////////////////////////////////////
commands.push("$jobGroup = [System.Guid]::NewGuid().ToString() ");
commands.push("$tempUUID1 = [System.Guid]::NewGuid().ToString()");

commands.push("$Template = Get-SCVMTemplate  -name $vmTemplateName");
commands.push("$osName= $Template.OperatingSystem.Name");
commands.push("$osType = $Template.OperatingSystem.OSType");
commands.push("$tempTemplate = New-SCVMTemplate -Name \"Temporary Template$jobGroup\" -Template $Template ");

commands.push(" if ( $osType -eq \"Linux\" ) { " );

// the scripts for setting DNS.
var linuxScripts = [];

//Temporary workaround for netmask issue - Ericsson
linuxScripts.push(" ' crontab -l | { cat; echo \"* * * * * /sbin/ifconfig eth1 netmask 255.255.0.0\"; } | crontab - ' ");

linuxScripts.push(" \" sed -i '/^search/d' /etc/resolv.conf \" ");
linuxScripts.push(" ' /bin/sh -c \"echo search  " + scriptletContext.get("pc.dns.machine.domain") + " >>/etc/resolv.conf\"  ' " );
linuxScripts.push(" \" sed -i '/^nameserver/d' /etc/resolv.conf \" ");
linuxScripts.push(" ' /bin/sh -c \"echo nameserver    " + scriptletContext.get("dns1") + ">> /etc/resolv.conf \"  ' ");
linuxScripts.push(" ' /bin/sh -c \"echo nameserver    " + scriptletContext.get("dns2") + ">> /etc/resolv.conf \" ' ");
//fix issue: append back the localhost entry in /etc/hosts.
linuxScripts.push(" ' /bin/sh -c \"echo 127.0.0.1 localhost >>/etc/hosts \" ' ");
commands.push(" 	$linuxScripts = " + linuxScripts.join(", ") );
//commands.push(" if ( $osName -match 'Red Hat' ) {");
//fqdn name for rhel
//commands.push(" $linuxScripts += \" sed -i '/^HOSTNAME/d' /etc/sysconfig/network \" ");
//commands.push(" $linuxScripts += \" /bin/sh -c 'echo HOSTNAME=$vmComputerName >>  /etc/sysconfig/network' \" ");

//commands.push("} elseif ( $osName -match 'Suse' ) { ");
//fqdn name for suse
//commands.push(" $linuxScripts += \" /bin/sh -c 'echo $vmComputerName > /etc/HOSTNAME' \" ");
//commands.push("}");
// fqdn name without reboot.
//commands.push(" $linuxScripts += \" /bin/sh -c 'hostname $vmComputerName' \" ");

commands.push(" }");

commands.push("$vmNetworkBUR = Get-SCVMNetwork -Name $vmNetworkBURName ");
commands.push("$vmNetworkCustomer = Get-SCVMNetwork -Name $vmNetworkCustomerName ");

// Get address pools.
commands.push(" $ipPool1 = Get-SCStaticIPAddressPool -Name $psVlan1 ");
commands.push(" $ipPool2 = Get-SCStaticIPAddressPool -Name $psBackupVlan ");
commands.push(" $ipPoolArray = $ipPool1,$ipPool2 ");

if ( vlan2ID != null) {
commands.push(" New-SCVirtualNetworkAdapter   -VMTemplate $tempTemplate  -Synthetic");
commands.push(" $ipPool3 =  Get-SCStaticIPAddressPool -Name $psVlan2 ");
commands.push(" $ipPoolArray += $ipPool3 ");
}

// Make sure the adapters are configured with static address type and mac address type.
commands.push("$nicInTemplate = Get-SCVirtualNetworkAdapter -VMTemplate $tempTemplate ");
commands.push(" foreach( $adapter in $nicInTemplate) { ");
commands.push(" Set-SCVirtualNetworkAdapter $adapter  -IPv4AddressType Static  -macAddressType Static -MACAddress \"00-00-00-00-00-00\" -VirtualNetwork $virtualNetwork -VMNetwork $vmNetworkBUR ");
commands.push(" }");


//#$LocalAdministratorCredential = get-credential
//#-LocalAdministratorCredential $LocalAdministratorCredential
commands.push("$vmHost = Get-SCVMHost -VMHostGroup ( get-SCVMHostGroup $hostGroupName ) | Sort-Object -Property VMs.count | Select-Object -First 1");
commands.push("$vmConfig = New-SCVMConfiguration -VMTemplate $tempTemplate  -VMHostGroup ( get-SCVMHostGroup $hostGroupName ) -Name \"Config$jobGroup\" ");
commands.push(" Set-SCVMConfiguration -VMConfiguration $vmConfig -ComputerName $vmComputerName -VMHost $vmHost -VMLocation $vmPath ");

// Update vlan and ip settings on the network adapters.

commands.push(" $ipInUseSet = Get-SCIPAddress ");
commands.push(" foreach ( $ip in $ipInUseSet  ) {");
commands.push("		$nic = Get-SCVirtualNetworkAdapter -id $ip.AssignedToID -ErrorAction SilentlyContinue ");
commands.push("		if ( ! $nic) {$ip | Revoke-SCIPAddress -ReturnToPool $true} "); 
commands.push(" } ");
commands.push(" $macAddressPool = Get-SCMACAddressPool -name $macAddressPoolName ");

commands.push(" $naConfigArray = Get-SCVirtualNetworkAdapterConfiguration  -VMConfiguration $vmConfig ");
commands.push(" $i = 0 ");
commands.push(" $j = 0 ");
commands.push(" foreach ( $naConfig in $naConfigArray ) { ");

commands.push(" 	$ipPool = $ipPoolArray[$i++] ");
commands.push(" 	$ip = $ipArray[$j++] ");
commands.push(" 	Set-SCVirtualNetworkAdapterConfiguration -VirtualNetworkAdapterConfiguration $naConfig  -IPv4Address $ip -IPv4AddressPool $ipPool  -PinIPv4AddressPool $true -MACAddressPool $macAddressPool -PinMACAddressPool $true  ");
commands.push("} ");
commands.push(" Update-SCVMConfiguration $vmConfig ");
commands.push(" $guestOSProfileName = $guestOSProfileHashTable[$osName] ");
var newVMCmdPart1=" $myVM = New-SCVirtualMachine -Name $vmName -VMConfiguration $vmConfig -cpucount $tshirtSize[\"cpu\"] -memorymb $tshirtSize[\"ram\"] -Description \"\" ";
var newVMCmdParte = " -BlockDynamicOptimization $false -JobGroup $jobGroup -StartAction \"NeverAutoTurnOnVM\" -StopAction \"SaveVM\" ";
commands.push(" if ( $guestOSProfileName ) { ");
commands.push("  	$guestOSProfile = Get-SCGuestOSProfile -Name $guestOSProfileName ");     
commands.push("     if ( $osType -eq \"Linux\" ) { ");
commands.push(			newVMCmdPart1 +" -GuestOSProfile $guestOSProfile -GuiRunOnceCommands $linuxScripts  -LinuxDomainName $vmDomainName  " + newVMCmdParte);
commands.push("     }else { " );
//commands.push(			newVMCmdPart1 + "	-GuestOSProfile $guestOSProfile " + newVMCmdParte);
commands.push(      newVMCmdPart1 + "-GuiRunOnceCommands \"cmd.exe /c timeout 60\", \"cmd.exe /c echo netsh interface ip set address name=\"\"Ethernet 2\"\" static $ipPool2 255.255.0.0 >c:\setNW.bat\", \"cmd.exe /c c:\setNW.bat > c:\ipconfig-test.txt\" -GuestOSProfile $guestOSProfile " + newVMCmdParte);
commands.push("     } ");
commands.push (" } else { ");
commands.push("     if ( $osType -eq \"Linux\" ) { ");
commands.push(			newVMCmdPart1 + "	 -GuiRunOnceCommands $linuxScripts  -LinuxDomainName $vmDomainName " + newVMCmdParte);
commands.push("     }else { " );
//commands.push(			newVMCmdPart1  + newVMCmdParte);
commands.push(      newVMCmdPart1 + "-GuiRunOnceCommands \"cmd.exe /c timeout 60\", \"cmd.exe /c echo netsh interface ip set address name=\"\"Ethernet 2\"\" static $ipPool2 255.255.0.0 >c:\setNW.bat\", \"cmd.exe /c c:\setNW.bat > c:\ipconfig-test.txt\" " + newVMCmdParte);
commands.push("     } ");
commands.push (" } ");



//We may remove the temporary template if anything goes wrong due to existing temporary template.

// set vmnetwork, vlan and virtualnetwork.
commands.push("$adapterArray = Get-SCVirtualNetworkAdapter -VM $myVM ");
commands.push(" foreach( $adapter in $adapterArray) { ");
commands.push("   if( $adapter.IPv4Addresses -match $psBackupIP ) { ");
//#backup vlan
commands.push(" Set-SCVirtualNetworkAdapter $adapter -vlanEnabled $true -VlanID $psBackupVlanID -virtualNetwork $virtualNetwork -VMNetwork $vmNetworkBUR ");
commands.push("   }elseif( $adapter.IPv4Addresses -match $psVlan1IP ) { ");
//# set cust vlan1.
commands.push(" Set-SCVirtualNetworkAdapter $adapter -vlanEnabled $true -VlanID $psVlan1ID -virtualNetwork $virtualNetwork -VMNetwork $vmNetworkCustomer ");
commands.push(" }elseif($adapter.IPv4Addresses -match $psVlan2IP) {");
commands.push(" Set-SCVirtualNetworkAdapter $adapter -vlanEnabled $true -VlanID $psVlan2ID -virtualNetwork $virtualNetwork -VMNetwork $vmNetworkCustomer ");
commands.push("} }");

//# set disks.
commands.push("$myVM = get-scvirtualmachine -name $vmName");
//#1. extend disk.
// extend only if the current disk size is smaller than the target osDisk size.
commands.push("$osHardDiskSize= get-scvirtualharddisk -VM $myVM | select-object -Property MaximumSize|select-object -First 1");
commands.push("$osHardDiskSizeGB = [math]::Ceiling( $osHardDiskSize.MaximumSize/1024/1024/1024)");
commands.push(" if ( $osHardDiskSizeGB -lt  $tshirtSize[\"osDisk\"] ) { ");
commands.push("		Get-SCVirtualDiskDrive -VM $myVM | where -Property VolumeType -like 'boot*' |");
commands.push("		Expand-SCVirtualDiskDrive -VirtualHardDiskSizeGB $tshirtSize[\"osDisk\"]");
commands.push(" }");
//#2. add disk
 //# add the first datadisk.
 //PSO January 2016: changed LUN from '0' to '2' and changed 'dynamic' to 'fixed'
commands.push("New-SCVirtualDiskDrive -Bus 0 -LUN 2 -VM $myVM -FileName \"datadisk0\" -SCSI -VirtualHardDiskSizeMB $tshirtSize[\"dataDisk\"] -fixed;");

commands.push("Start-VM $myVM");
scriptletContext.put("commands",commands.join("\r\n"));

/*
   This scriplet constructs the command lines to be executed in SCVMM for deploying a VM. 
   Generally, it consists of the following steps:
   1. Input Data Preparation. Get the inputs from OO Input and do the ncessary conversion.
   2. Create a temporary VM Template, and set the parameters - Hostname, VMLocation, IP addresses.
