commands.push("     } ");
commands.push (" } ");



//We may remove the temporary template if anything goes wrong due to existing temporary template.

// set vmnetwork, vlan and virtualnetwork.
commands.push("$adapterArray = Get-SCVirtualNetworkAdapter -VM $myVM ");
