commands.push("$osHardDiskSizeGB = [math]::Ceiling( $osHardDiskSize.MaximumSize/1024/1024/1024)");
commands.push(" if ( $osHardDiskSizeGB -lt  $tshirtSize[\"osDisk\"] ) { ");
commands.push("		Get-SCVirtualDiskDrive -VM $myVM | where -Property VolumeType -like 'boot*' |");
commands.push("		Expand-SCVirtualDiskDrive -VirtualHardDiskSizeGB $tshirtSize[\"osDisk\"]");
commands.push(" }");
//#2. add disk
 //# add the first datadisk.
 //PSO January 2016: changed 'dynamic' to 'fixed'
commands.push("New-SCVirtualDiskDrive -Bus 0 -LUN 0 -VM $myVM -FileName \"datadisk0\" -SCSI -VirtualHardDiskSizeMB $tshirtSize[\"dataDisk\"] -fixed;");

commands.push("Start-VM $myVM");
scriptletContext.put("commands",commands.join("\r\n"));
