staticBinding.value:
cat >/etc/multipath.conf <<EOL
# HP Enterprise Services standard multipath configuration

defaults {
    user_friendly_names yes
    bindings_file /etc/multipath/bindings
    rr_min_io 1
    polling_interval 10
    max_fds 8192
}

# ``HP 3PAR Red Hat Enterprise Linux and Oracle Linux Implementation Guide'' (QL226-97991)
devices {
    device {
        vendor "3PARdata"
        product "VV"
        features "0"
        hardware_handler "1 alua"
        path_selector "round-robin 0"
        getuid_callout "/sbin/scsi_id -g -u -s /block/%n"
        path_grouping_policy "group_by_prio"
        prio_callout "/sbin/mpath_prio_alua /dev/%n"
        failback "immediate"
        rr_weight "uniform"
        no_path_retry 18
        rr_min_io 100
        path_checker "tur"
    }
}

blacklist {
    device {
        vendor "HP"
        product "LOGICAL VOLUME"
    }
    device {
        vendor "VMware"
        product "Virtual disk"
    }
    device {
        vendor "Msft"
        product "Virtual Disk"
    }
    device {
        vendor "ATA"
        product "QEMU HARDDISK"
    }
}
EOL

/sbin/multipath -F

/opt/hp/hp_fibreutils/hp_rescan -a 2>&1 >/dev/null;
/bin/sleep 10;
if  grep -q SLES /etc/*release 
then
/etc/init.d/multipathd start 2>&1 >/dev/null;
fi
/sbin/multipath -ll | /bin/grep -i ${lun.wwname} | /bin/awk '{ print $1 }'
