staticBinding.value:
$diskNumbers = "${diskNumberList}"
$diskNumberArray = $diskNumbers.split(",")

$diskSummary = New-Object PSObject

forEach($diskNumber in $diskNumberArray){

    $filter = "name=`"Disk #$diskNumber, Partition #0`""

    $partitions = gwmi Win32_DiskPartition -filter $filter;

    if($partitions -ne $null){

        $partDetails = $partitions.GetRelated('Win32_LogicalDisk')

        $driveLabel = $partDetails.DeviceID;
        $diskLabel = $partDetails.VolumeName;

        $disk = New-Object PSObject
        Add-Member -InputObject $disk -MemberType NoteProperty -Name "driveLetter" -Value $driveLabel
        Add-Member -InputObject $disk -MemberType NoteProperty -Name "diskLabel" -Value "$diskLabel"

    }
    else{
        $disk = New-Object PSObject
        Add-Member -InputObject $disk -MemberType NoteProperty -Name "driveLetter" -Value $null
        Add-Member -InputObject $disk -MemberType NoteProperty -Name "diskLabel" -Value "$null"
    }

    Add-Member -InputObject $diskSummary -MemberType NoteProperty -Name "disk$diskNumber" -Value $disk
}


$diskJson = ConvertTo-Json -InputObject $diskSummary
echo "$diskJson"
