staticBinding.value:
$strDomainName = CMD.EXE /C "$strSysDrive\Program Files\Opsware\agent_tools\get_cust_attr.bat" windows_domainname
$strDomainOU = CMD.EXE /C "$strSysDrive\Program Files\Opsware\agent_tools\get_cust_attr.bat" windows_domainou

Try{
	echo "Creating new Cluster Name Object..."
	New-ADComputer -Name "${clusterName}" -SamAccountName "${clusterName}" -Path "$strDomainOU"

	$clusterDN = "CN=${clusterName},$strDomainOU"
	echo "Disable Cluster Name Object"
	Disable-ADAccount -Identity "$clusterDN"
	exit 0;
}
Catch
{
	$ErrorMessage = $_.Exception.Message
	$FailedItem = $_.Exception.ItemName
	echo "Unable to prestage Cluster object ${clusterName} - $ErrorMessage, $FailedItem"
	exit 1;
}
