staticBinding.value:
function installFeaturesOnNodes
{
	Param(
		[String]$hostname
	)

	echo "Trying to install Failover Cluster feature on host $hostname"

	$CheckFeature = IsFeatureexist $hostname
	If(-not $CheckFeature)
    {
		echo "Installing Cluster Feature on : $hostname"
		Writelog "Installing Cluster Feature on : $hostname"

		Try
		{
			Install-WindowsFeature -Name Failover-Clustering -computerName $hostname -IncludeManagementTools -ErrorAction stop | Out-null
			Start-Sleep -Seconds 20
		}
		Catch
		{
			$ErrorMessage = $_.Exception.Message
			WriteLog "Unable to install cluster feature on : $hostname with error :$ErrorMessage"  2
			echo "Unable to install cluster feature on : $hostname with error :$ErrorMessage"
			Fail	
		}
		echo "Successfully installed Failover Cluster Feature on host $hostname"
	}
	Else
	{
		Writelog "ClusterFeature is already installed on $hostname"
		echo "ClusterFeature is already installed on $hostname"
	}
}


Function Fail
{
        WriteLog "Failed to install Failover Cluster Feature"
        Write-Host "Script exectuion Failed"
		echo "Script exectuion Failed"
		Exit 1
}

function WriteLog 
{
    param ([string]$text, [int]$flag)
    $date = get-date -format MM:dd:yyyy-HH:mm:ss
    if ($flag -eq 0) 
    {
       Write-Output "$date INFO: $text" | Out-File $logfile -append
    } 
	elseif ($flag -eq 1)
    {
        Write-Output "$date WARNING: $text" | Out-File $logfile -append
    } 
    elseif ($flag -eq 2) 
    {
        Write-Output "$date ERROR: $text" | Out-File $logfile -append
    } 
    elseif ($flag -eq 3) 
    {
        Write-Output "$date DEBUG: $text" | Out-File $logfile -append
    }
    elseif ($flag -eq 4 ) 
    {
        Write-Output "$text" | Out-File $logfile -append
    }
    else
    {
        Write-Output "$text" | Out-File $logfile -append
    }
    If ($Echo -eq "True" ) 
    {
	Write-Host $text
    }
}

Function IsFeatureexist
{
    Param (
		[Parameter(Mandatory=$true)]$Servername
	)
    $ClusterFeature = Get-WindowsFeature "Failover-Clustering" -ComputerName $Servername 

    If($ClusterFeature.InstallState -eq "Installed")
    {
       return $true
    }
    Else
    {
        return $false
    }
}

$Presentdate = get-date -format MM-dd-yyyy
$logfile = "c:\temp\OS_Clustering_InstallFeatures_$Presentdate.log"

installFeaturesOnNodes -hostname "${hostname}"

